import React, { Component } from 'react';
import { Link } from 'react-router-dom';
import axios from 'axios';

const Release = props => (
    <tr>
        <td className={props.release.release_completed ? 'completed' : ''}>{props.release.release_title}</td>
        <td className={props.release.release_completed ? 'completed' : ''}>{props.release.release_band}</td>
        <td className={props.release.release_completed ? 'completed' : ''}>{props.release.release_format}</td>
        <td className={props.release.release_completed ? 'completed' : ''}>{props.release.release_listened}</td>
        <td>
            <Link to={"/edit/"+props.release._id}>Edit</Link>
        </td>
    </tr>
)

export default class TodosList extends Component {

    constructor(props) {
        super(props);
        this.state = {releases: []};
    }

    componentDidMount() {
        axios.get('http://localhost:4000/releases/')
            .then(response => {
                this.setState({ releases: response.data });
            })
            .catch(function (error){
                console.log(error);
            })
    }

    todoList() {
        return this.state.releases.map(function(currentTodo, i){
            return <Release release={currentTodo} key={i} />;
        })
    }

    render() {
        return (
            <div>
                <h3>Releases List</h3>
                <table className="table table-striped" style={{ marginTop: 20 }} >
                    <thead>
                        <tr>
                            <th>Description</th>
                            <th>Responsible</th>
                            <th>Priority</th>
                            <th>Action</th>
                        </tr>
                    </thead>
                    <tbody>
                        { this.todoList() }
                    </tbody>
                </table>
            </div>
        )
    }
}
