import React, { Component } from 'react';
import axios from 'axios';

export default class CreateRelease extends Component {

    constructor(props) {
        super(props);

        this.onChangeReleaseTitle = this.onChangeReleaseTitle.bind(this);
        this.onChangeReleaseBand = this.onChangeReleaseBand.bind(this);
        this.onChangeReleaseYear = this.onChangeReleaseYear.bind(this);
        this.onChangeReleaseFormat = this.onChangeReleaseFormat.bind(this);
        this.onSubmit = this.onSubmit.bind(this);

        this.state = {
            release_title: '',
            release_band: '',
            release_format: '',
            release_year: ''
        }
    }

    onChangeReleaseTitle(e) {
        this.setState({
            release_title: e.target.value
        });
    }

    onChangeReleaseBand(e) {
        this.setState({
            todo_responsible: e.target.value
        });
    }

    onChangeReleaseFormat(e) {
        this.setState({
            todo_priority: e.target.value
        });
    }

    onChangeReleaseYear(e) {
        this.setState({
            todo_priority: e.target.value
        });
    }

    onSubmit(e) {
        e.preventDefault();

        console.log(`Form submitted:`);
        console.log(`Release Title ${this.state.release_title}`);
        console.log(`Release Band: ${this.state.todo_responsible}`);
        console.log(`Release Year: ${this.state.todo_priority}`);
        console.log(`Release Format: ${this.state.todo_priority}`);

        const newRelease = {
            release_title: this.state.release_title,
            todo_responsible: this.state.todo_responsible,
            todo_priority: this.state.todo_priority,
            todo_completed: this.state.todo_completed
        };

        axios.post('http://localhost:4000/releases/add', newTodo)
            .then(res => console.log(res.data));

        this.setState({
            todo_description: '',
            todo_responsible: '',
            todo_priority: '',
            todo_completed: false
        })
    }

    render() {
        return (
            <div style={{marginTop: 10}}>
                <h3>Create New Release</h3>
                <form onSubmit={this.onSubmit}>
                    <div className="form-group"> 
                        <label>Description: </label>
                        <input  type="text"
                                className="form-control"
                                value={this.state.todo_description}
                                onChange={this.onChangeTodoDescription}
                                />
                    </div>
                    <div className="form-group">
                        <label>Responsible: </label>
                        <input 
                                type="text" 
                                className="form-control"
                                value={this.state.todo_responsible}
                                onChange={this.onChangeTodoResponsible}
                                />
                    </div>
                    <div className="form-group">
                        <div className="form-check form-check-inline">
                            <input  className="form-check-input" 
                                    type="radio" 
                                    name="priorityOptions" 
                                    id="priorityLow" 
                                    value="Low"
                                    checked={this.state.todo_priority==='Low'} 
                                    onChange={this.onChangeTodoPriority}
                                    />
                            <label className="form-check-label">Low</label>
                        </div>
                        <div className="form-check form-check-inline">
                            <input  className="form-check-input" 
                                    type="radio" 
                                    name="priorityOptions" 
                                    id="priorityMedium" 
                                    value="Medium" 
                                    checked={this.state.todo_priority==='Medium'} 
                                    onChange={this.onChangeTodoPriority}
                                    />
                            <label className="form-check-label">Medium</label>
                        </div>
                        <div className="form-check form-check-inline">
                            <input  className="form-check-input" 
                                    type="radio" 
                                    name="priorityOptions" 
                                    id="priorityHigh" 
                                    value="High" 
                                    checked={this.state.todo_priority==='High'} 
                                    onChange={this.onChangeTodoPriority}
                                    />
                            <label className="form-check-label">High</label>
                        </div>
                    </div>

                    <div className="form-group">
                        <input type="submit" value="Create Release" className="btn btn-primary" />
                    </div>
                </form>
            </div>
        )
    }
}
